-define(UP, $().
-define(DN, $)).

-spec part1() -> integer().
part1() ->
    read_instructions(?INPUT).

-spec part2() -> integer().
part2() ->
    get_basement_position(?INPUT).

-spec read_instructions(string()) -> integer().
read_instructions(Instructions) ->
    read_instructions(Instructions, 0).

-spec get_basement_position(string()) -> integer().
get_basement_position(Instructions) ->
    get_basement_position(Instructions, 0, 1).

% ==================
% Internal Functions
% ==================
-spec read_instructions(string(), integer()) -> integer().
read_instructions([], Floor) ->
    Floor;
read_instructions([H|T], Floor) ->
    NextFloor = case H of
        ?UP -> Floor + 1;
        ?DN -> Floor - 1;
        _ -> Floor
    end,
    read_instructions(T, NextFloor).

get_basement_position([], _, _) ->
    -1;
get_basement_position([H|T], Floor, Position) ->
    case H of
        ?UP ->
            get_basement_position(T, Floor + 1, Position + 1);
        ?DN ->
            case Floor - 1 of
                F when F < 0 ->
                    Position;
                NextFloor ->
                    get_basement_position(T, NextFloor, Position + 1)
            end;
        _ ->
            get_basement_position(T, Floor, Position + 1)
    end.

% =====
% Tests
% =====
instructions_test_() ->
    Tests = [{0, "(())"},
             {0, "()()"},
             {3, "))((((("},
             {-1, "())"},
             {-1, "))("},
             {-3, ")))"},
             {-3, ")())())"}],
    [?_assertEqual(Floor, read_instructions(Input)) || {Floor, Input} <- Tests].

basement_test_() ->
    Tests = [{1, ")"},
             {5, "()())"},
             {-1, "(()"}],
    [?_assertEqual(Pos, get_basement_position(Input)) || {Pos, Input} <- Tests].
